sub untaint {
    my $x = shift;
    ($x) = ($x =~ /(.*)/s); # untaint
    return $x;
}

sub parseConfig {
    my $ddserverJsonText, %ddserverJson;
    open (INPUT, "ddserver.json") || die "Could not open ddserver.json: $!";
    $ddserverJsonText = do { local $/; <INPUT> };
    close(INPUT);
    $ddserverJsonText = minify(input => $ddserverJsonText);
    %ddserverJson = %{decode_json $ddserverJsonText};
    %config = %{$ddserverJson{"config"}};

	if (-f "ddserver-hostname.conf") {
	    open (INPUT, "ddserver-hostname.conf") || die "Could not open ddserver-hostname.conf: $!";
	    $ddserverHostname = do { local $/; <INPUT> };
	    close(INPUT);
	}
   
}

# evaluate an ASP/JSP/EJS-like string 
sub evalTemplate {
    my $src = shift;
    my ($eval, $pre, $post, $inmlb, $output);
    #print "src is $src\n";
    $eval = "";
    $out = "";
    while ($src =~ m/\G(.*?)<%/sgc) {
	    $pre = $1;
	    $pre =~ s/'/\\'/g; $pre =~ s/\n/'."\\n";\n\$out .= '/gs;
	    if ($pre ne "") { $eval .= "\$out .= '" . $pre . "';\n"; }
	    if ($src =~ m/\G=(.*?)%>/gc) {
	        $eval .= "\$out .= " . $1 . ";\n";
	        
	    } elsif ($src =~ m/\G(.*?)%>/gsc) {
	        $eval .= $1 . "\n";
	      
	    } else {
	        # '<%' without matching '%>'; could raise a syntax error but will just pass it through
		    $eval .= "\$out .= '<%'\n";
	    } 
    }
    if ($src =~ m/\G(.+)$/s) {
	    $post = $1;
	    $post =~ s/'/\\'/g; $post =~ s/\n/'."\\n";\n\$out .= '/gs;
	    $eval .= "\$out .= '" . $post . "';\n";
    }
  
    #print "eval is $eval\n===================\n";
    #$eval =~ s/\$out .= '';\n//g;
    
    eval $eval;
    if ($@) { die "Error in template: " . $@ };
    return $out;
}





