    } elsif ($pathInfo eq "/update" || $action eq "updateForm") {
        if ($pathInfo eq "/update") { $interface=IFACE_DYNDNS; $sendErrorsAsInscrutableErrorCode = 1; }
        
        # some ACL checks here 
        $auth = "";
        
        if (defined $q->param("username") && defined $q->param("password")) {
