        }
        if (! $domain) {
            printlog "Unknown domain '$hostname'";
            %result=("html"=>"Unknown domain '$hostname'", "dyndns"=>"nohost", "zoneedit"=>"?");
            die "nohost"; 
        }


        # determine new SOA serial number
        my ($sec,$min,$hour,$mday,$mon,$year,$wday,$yday,$isdst) = localtime(time);
        $serialTimestamp = sprintf("%04d%02d%02d", ($year+1900), ($mon+1), $mday);
        $serial = sprintf("%s%02d", $serialTimestamp, 1); 
      
        $bindfilename = $config{"bindZoneFile"};
        $bindfilename =~ s/{zone}/$domain/;
        $bindtemplatefilename = $config{"bindZoneTemplateFile"};
        $bindtemplatefilename =~ s/{zone}/$domain/;
        
        if (-f $bindfilename) {
	        open (INPUT, $bindfilename) || die "Could not open $bindfilename: $!";
	        $bindfile = do { local $/; <INPUT> };
	        $oldbindfile = $bindfile; # for backups
	        close(INPUT);

            # what could possibly go wrong here
            if (($bindrecords) = ($bindfile =~ m/
	          ^;\s+===\s+START\s+OF\s+GENERATED\s+CONFIGURATION\n
