	              (.*)
	              (^;\s+===\s+END\s+OF\s+GENERATED\s+CONFIGURATION\n)
	              /$1<%= \$bindrecords %>\n$3/msx;
	            chomp $bindrecords;  
            } else {
            	die "No generated configuration found in $bindfilename";
