		        $bindfile = do { local $/; <INPUT> };
		        close(INPUT);
		        # we don't care about the serial number in the template
		        if (($startOfSoaRecord, $oldserial_ignored) = ($bindfile =~ m/^(\@\s+IN\s+SOA\s+\S+\s+\S+\s+\(\s+)([0-9]+)/mp)) {
                	$bindprematch = ${^PREMATCH}; $bindmatch = ${^MATCH}; $bindpostmatch = ${^POSTMATCH};
            	} else {
                	die "Can't determine existing serial number in '$bindfilename'";
            	}
		    }
          
			# update serial			
            if ($oldserial=~/^$serialTimestamp([0-9]{2})/) {
                $nn = $1; $nn++; if ($nn == 100) { $mday++; $serialTimestamp = sprintf("%04d%02d%02d", ($year+1900), ($mon+1), $mday); }
                $serial = sprintf("%s%02d", $serialTimestamp, $nn);
              
            } elsif ($oldserial lt $serialTimestamp) {
                # first update for today
                $serial = sprintf("%s%02d", $serialTimestamp, 1); 
              
            } else {
                # we appear to be going backwards in time
                #$serial = sprintf("%10d", $oldserial + 1); 
                # die "The times they are a-changing. Replenish the sand in your NTP server or something (oldserial=$oldserial; serialTimestamp=$serialTimestamp).";
                %result=("html"=>"Internal error", "dyndns"=>"911", "zoneedit"=>"?");
                die "Old serial is in the future (oldserial=$oldserial; serialTimestamp=$serialTimestamp)";
            }

            $bindfile = $bindprematch . $startOfSoaRecord . $serial . $bindpostmatch;
          
            $backupbindfilename = $config{"bindZoneBackupFile"};
            $backupbindfilename =~ s/{zone}/$domain/;  
            $backupbindfilename =~ s/{serial}/$oldserial/;
            
            printlog "$domain: serial updated from $oldserial to $serial";
            #print "backup will be in $backupbindfilename; oldserial=$oldserial; serial=$serial\n";
          
        } else {
            printlog "$domain: new zonefile";
