				                    }
			                    } else {
			                        splice(@lines, $i, 1); $i--;
			                        printlog "$domain: A record removed for '$host'";
			                        %result=("html"=>"DNS 'A' record removed for '$host.$domain'",
			                                 "dyndns"=>"good", "zoneedit"=>"?");
			                    }
		                    } else {
		                    	$oldip = $rrData;
		                    }     
		                #} elsif ($rrClass eq "MX") {
		                #    if ($mx ne "NOCHG") { 
			            #        if ($mx) {
			            #            $line[$i] = $mxRecord;
			            #            $updatedMX = 1;
