function escapeHtml(text) {
  text=text.replace(/&/g, "&amp;");
  text=text.replace(/</g, "&lt;");
  text=text.replace(/>/g, "&gt;");
  return text;
}

function setDomain(domain) {
  $("#domain").attr("value", domain); 
  $("#domainText").html(domain); 
}

__EODDSERVERJS
}

